import React, { useState } from "react";
import Buttons from "./Buttons";
import quotes from "./quotes.js";

function RandomQuote() {
  const [quote, setQuote] = useState({
    currentQuote:
      "The only person you are destined to become is the person you decide to be.",
    currentAuthor: "Ralph Waldo Emerson",
  });

  function getRandomQuote() {
    return quotes[Math.floor(Math.random() * quotes.length)];
  }

  function getQuote() {
    let randomQuote = getRandomQuote();
    let newQuote = randomQuote.quote;
    let newAuthor = randomQuote.author;
    return newQuote, newAuthor;
  }
  function changeQuote() {
    setQuote({
      quote: newQuote,
      author: newAuthor,
    });
  }

  return (
    <div id='quote-box' className='quote-box'>
      <div id='text'>
        <span>{quote.currentQuote}</span>
      </div>
      <div id='author'>
        <span>- {quote.currentAuthor}</span>
      </div>
      <Buttons onClick={changeQuote} />
    </div>
  );
}

export default RandomQuote;
